INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('2019 Porsche Gt3 Rs', 'Porsche', 2019, 1850000, 8000, 'https://images.squarespace-cdn.com/content/v1/5e7918a4b1051f6e49dcfdb1/1586044928090-SH1NKERMTJU5Z3SSR9Z7/gt3-rs-green-3_1920-1024x576.jpg', 'https://images.hgmsites.net/hug/2019-porsche-911-gt3-rs_100645800_h.jpg', 'https://www.americanmusclecarmuseum.com/files/cars/2019-porsche-gt3-rs-weissach-edition_1014.jpg', 'https://images.squarespace-cdn.com/content/v1/5e7918a4b1051f6e49dcfdb1/1586044928677-JYCSMF8YONEOQKOGOR1H/side-shot-1.jpg', 'São Paulo, São Paulo', 'Novos', ' Experimente a adrenalina pura com este Porsche GT3 RS 2019, um ícone de desempenho com apenas 8.000 km rodados. Perfeito para quem busca emoção e precisão na pista e nas ruas.', 'Este Porsche é um exemplar impecável de engenharia alemã, projetado para oferecer uma experiência de condução inigualável. Design aerodinâmico e chassi otimizado garantem uma agilidade surpreendente e uma aderência excepcional em qualquer condição.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('2019 Ford GT MK II', 'Ford', 2019, 6500000, 1500, 'https://cdn.motor1.com/images/mgl/pOZXP/s1/ford-gt-mkii-2019.jpg', 'https://cdn.motor1.com/images/mgl/3Xx7p/s1/ford-gt-mkii-2019.jpg', 'https://cdn.motor1.com/images/mgl/7wgYq/s1/ford-gt-mkii-2019.jpg', 'https://i.bstr.es/highmotor/2019/07/ford-gt-mk-ii-2-880x587.jpg', 'Porto Alegre, Rio Grande do Sul', 'Novos', 'Apresentamos o Ford GT MK II 2019, uma máquina de corrida homologada para as pistas, com apenas 1.500 km rodados. Exclusividade e performance levadas ao extremo. ', 'Este Ford é mais do que um carro; é uma obra-prima da engenharia automotiva, projetada para dominar as pistas de corrida. Este veículo é uma raridade, oferecendo desempenho inigualável e uma experiência de condução visceral.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('2019 Chevrolet Corvette ZR1', 'Chevrolet', 2019, 1600000, 12000, 'https://cdn.motor1.com/images/mgl/OR1qe/s1/4x3/2019-chevrolet-corvette-zr1.webp', 'https://resizer.iproimg.com/unsafe/1280x/filters:format(webp):quality(85):max_bytes(102400)/https://assets.iprofesional.com/assets/jpg/2017/11/450685.jpg', 'https://media.whichcar.com.au/uploads/2025/02/56f4a13e-2018-Chevrolet-Corvette-ZR1-performance-review-1024x683.jpg', 'https://www.razaoautomovel.com/wp-content/uploads/2017/11/2018-chevy-corvette-zr1_12.jpg.webp', 'Porto Alegre, Rio Grande do Sul', 'Usados', 'Domine as estradas com este Chevrolet Corvette ZR1 2019, uma máquina americana com 12.000 km. Potência brutal e design agressivo para os verdadeiros entusiastas.', 'O Chevrolet é a personificação da força americana, um supercarro projetado para entregar desempenho de pista com a capacidade de uso diário. Este ZR1 oferece um motor V8 supercharged que entrega uma potência estrondosa e um torque imediato. ');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Nissan 370z', 'Nissan', 2020, 350000, 25000, 'https://media.carsandbids.com/cdn-cgi/image/width=2080,quality=70/6603345104d15ba9217527fd6060c44679292dd9/photos/rklQEeXl.B1vfZvMcn-(edit).jpg?t=162871724537', 'https://uploads.builtforbackroads.com/uploads/2022/10/2022.10.31-NISSAN-370Z-2020_7.jpg', 'https://cdn.dealeraccelerate.com/vx/1/158/2211/1920x1440/2020-nissan-370z-coupe-nismo-manual', 'https://images.customwheeloffset.com/web-compressed/1787464-1-2020-370z-nissan-nismo-bc-racing-coilovers-bc-forged-mle72-black.jpg', 'Rio de Janeiro, Rio de Janeiro', 'Usados', 'Um clássico moderno, este Nissan 370Z 2020 com 25.000 km oferece uma experiência de condução esportiva e envolvente, perfeita para o dia a dia e para a diversão.', 'Este Nissan é a escolha ideal para quem busca um carro esportivo com design icônico, desempenho comprovado e a confiabilidade japonesa. Um motor V6 potente, combinado com uma transmissão que proporciona trocas de marcha precisas e emocionantes.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Lamborghini Huracán EVO', 'Lamborghini', 2021, 3400000, 9500, 'https://i.ytimg.com/vi/T_2aG8_xplk/maxresdefault.jpg', 'https://gvelondon.com/wp-content/uploads/2024/07/20240702-9-2-1-scaled.jpg', 'https://http2.mlstatic.com/D_NQ_NP_947077-MLB70135484984_062023-O-lamborghini-huracan-evo-red-autoart-miniatura-118.webp', 'https://infinityrentalluxury.com/wp-content/uploads/2024/07/01-4-X3-768x513.jpg', 'Belo Horizonte, Minas Gerais', 'Usados', 'Sinta a emoção pura deste Lamborghini Huracán EVO 2021. Com 9.500 km, este superesportivo italiano oferece um motor V10 aspirado e um design que para o trânsito.', 'Este Huracán EVO é a expressão máxima da potência e do design da Lamborghini. Equipado com um sistema de aerodinâmica ativa e tração integral, ele proporciona uma condução precisa e visceral. O interior luxuoso combina com a performance.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Audi RS6 Avant', 'Audi', 2022, 980000, 15000, 'https://alpes-hub.s3.amazonaws.com/uploads/public/67f/554/375/67f554375927a266057979.webp', 'https://cdn.motor1.com/images/mgl/g4MJbN/s1/audi-rs6-avant-performance.jpg', 'https://cdn.autopapo.com.br/box/uploads/2024/03/12181210/audi-rs6-avant-performance-2024-preto-traseira-parado-estrada.png', NULL, 'Curitiba, Paraná', 'Usados', 'Audi RS6 Avant 2022: a combinação perfeita de um superesportivo e a versatilidade de uma perua. Com 15.000 km, é o carro para quem quer tudo.', 'Equipado com um poderoso motor V8 Biturbo, este Audi RS6 Avant entrega aceleração de supercarro sem sacrificar o espaço e o conforto. Tração integral Quattro e um interior tecnológico tornam qualquer viagem uma experiência única.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('McLaren 720S', 'McLaren', 2020, 2950000, 7000, 'https://mclaren.scene7.com/is/image/mclaren/McLaren_720s_Shot_14_Static_Exterior_Coupe_Dead_Rear_R41:crop-16x9?wid=1786&hei=1005', 'https://mclaren.scene7.com/is/image/mclaren/720S-Coupe_hero:crop-16x9?wid=1920&hei=1080', NULL, NULL, 'Brasília, Distrito Federal', 'Novos', 'McLaren 720S 2020 com 7.000 km. Uma obra de arte da engenharia britânica, projetada para performance extrema e um visual de tirar o fôlego.', 'Construído sobre um chassi monocoque de fibra de carbono, o 720S é incrivelmente leve e rígido. Seu motor V8 biturbo oferece uma aceleração brutal, transformando cada segundo ao volante em pura adrenalina. Uma experiência única de pilotagem.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('BMW M2 Competition', 'BMW', 2021, 480000, 18000, 'https://mediapool.bmwgroup.com/cache/P9/201803/P90298653/P90298653-the-new-bmw-m2-competition-04-2018-600px.jpg', NULL, NULL, NULL, 'Salvador, Bahia', 'Usados', 'BMW M2 Competition 2021, com 18.000 km. O puro prazer de dirigir em um pacote compacto e musculoso, ideal para entusiastas que valorizam a dinâmica.', 'Este M2 Competition é a essência do que um BMW M deve ser. Com um motor de seis cilindros em linha biturbo e um chassi perfeitamente equilibrado, ele oferece uma conexão direta entre o motorista e a estrada. A agilidade é seu sobrenome.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Porsche Cayenne Turbo GT', 'Porsche', 2023, 1950000, 5000, 'https://images-porsche.imgix.net/-/media/FB7584094BB144F798306A8EDC665653_20125F01111242A48C812A9FC5116E06_cayenne-turbo-gt-cockpit-desktop?w=1299&q=85&auto=format', 'https://p2.trrsf.com/image/fget/cf/774/0/images.terra.com/2023/04/18/s23_0376-t4yzs4753acy.jpg', 'https://images-porsche.imgix.net/-/media/E4CE2F28FDC24BEDBCFC30DF38846B42_B31EAB6C1C4146C28CA67EA6EE2E6562_CY24J5SOX0006-chassis?w=1759&q=85&auto=format', 'https://d20f7dynuzdeeg.cloudfront.net/pdkmotors/20014/foto-Album-de-PORSCHE-CAYENNE-A17719-6684500aa0e93.webp', 'Goiânia, Goiás', 'Novos', 'O SUV de performance definitiva. Este Porsche Cayenne Turbo GT 2023, com 5.000 km, redefine o que é possível em um utilitário esportivo de altíssimo luxo.', 'Este Cayenne não é apenas um SUV, é um superlativo. Detentor de recordes em Nürburgring, seu motor V8 biturbo, chassi de carbono e freios de cerâmica oferecem uma experiência de condução que desafia a física, com luxo e tecnologia de ponta.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Mercedes-AMG GT Black Series', 'Mercedes-Benz', 2021, 4800000, 2500, 'https://www.europeanprestige.co.uk/blobs/stock/394/images/0a1e0cb2-45c1-4249-9564-4ea0d13c2ae1/hi4a9303.jpg?width=2000&height=1333', 'https://preview.redd.it/7e1kr76p1mk71.jpg?width=1080&crop=smart&auto=webp&s=7817fdd7aa7eeaf33a637f74cac29249fd00fda1', 'https://revistafullpower.com.br/wp-content/uploads/2021/09/me02.jpg', NULL, 'Florianópolis, Santa Catarina', 'Novos', 'Exclusividade e performance de pista. O Mercedes-AMG GT Black Series 2021 é um carro de corrida para as ruas, com apenas 2.500 km. Uma raridade absoluta.', 'Projetado com aerodinâmica ativa inspirada no automobilismo e o motor V8 de produção mais potente da AMG, este Black Series é a expressão máxima de performance. Cada detalhe foi otimizado para a velocidade, oferecendo uma conexão visceral com o asfalto.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Dodge Challenger Hellcat', 'Dodge', 2020, 750000, 19000, 'https://automotiva.com.ar/wp-content/uploads/2018/06/Dodge_Challenger_SRT_Hellcat_2019-5.jpg', 'https://cdn.motor1.com/images/mgl/0byVn/s1/2019-dodge-challenger-srt.jpg', NULL, NULL, 'Manaus, Amazonas', 'Usados', 'A pura força do muscle car americano. Este Dodge Challenger Hellcat 2020 com 19.000 km é a definição de potência bruta com seu motor HEMI Supercharged.', 'Viva a experiência de mais de 700 cavalos de potência em um dos carros mais icônicos da atualidade. Com um design intimidador e o som inconfundível do seu motor V8, este Hellcat foi feito para quem não tem medo de adrenalina e performance.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Honda Civic Type R', 'Honda', 2022, 310000, 22000, 'https://cdn.motor1.com/images/mgl/Oo376A/s1/honda-civic-type-r---brasil.jpg', NULL, NULL, NULL, 'Recife, Pernambuco', 'Usados', 'O hot hatch definitivo para entusiastas. Este Honda Civic Type R 2022, com 22.000 km, oferece precisão, performance e um design arrojado para as ruas e pistas.', 'Reconhecido mundialmente por sua dinâmica de condução excepcional e motor VTEC Turbo, o Type R é um esportivo de tração dianteira que entrega confiança e diversão em cada curva. Um carro prático para o dia a dia com alma de competidor.');