INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Ferrari SF90 Stradale', 'Ferrari', 2023, 6900000, 3000, 'https://cdn.motor1.com/images/mgl/xqq24z/s1/novitec-ferrari-sf90-stradale-2022.webp', 'https://res.cloudinary.com/unix-center/image/upload/c_limit,dpr_3.0,f_auto,fl_progressive,g_center,h_580,q_75,w_906/fl7hxq9rczontkgsvvw0.jpg', 'https://ferrari-cdn.thron.com/delivery/public/image/ferrari/95eb3f86-7461-436f-b053-4e76232dfc75/3zayf6/std/0x415/0x0-Ferrari_SF90_Stradale_4', null, 'Belo Horizonte, Minas Gerais', 'Novos', 'Um híbrido de 1000 cv que redefine o conceito de supercarro. Esta Ferrari SF90 Stradale 2023 com 3.000 km é a vanguarda da tecnologia e performance, diretamente das pistas para você.', 'Este exemplar representa o auge da engenharia da Ferrari, combinando um motor V8 biturbo a três motores elétricos para uma aceleração incomparável e uma eficiência surpreendente. ');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('BMW M3 Competition', 'BMW', 2023, 790000, 8000, 'https://www.bmw-m.com/content/dam/bmw/marketBMW_M/www_bmw-m_com/all-models/m-automobile/2020/2024/bmw-m3-competition-posi-disclaimer-ig-01-16x9.jpg', 'https://bmw.scene7.com/is/image/BMW/g80_competition_dynamics_fb_de?wid=3840&hei=2160', 'https://img.autocarindia.com/ExtraImages/20240530123943_2024_BMW_M3_Sedan_rear.jpg?w=700&c=1', 'https://bmw.scene7.com/is/image/BMW/g80_competition_financial-services?wid=3000&hei=3000', 'Curitiba, Paraná', 'Novos', 'O sedã esportivo em sua forma mais pura. Este BMW M3 Competition 2023 com 8.000 km combina performance de pista com usabilidade diária, um ícone de engenharia alemã.', 'Equipado com um motor de seis cilindros em linha M TwinPower Turbo de alta rotação, este M3 Competition entrega potência e torque de forma explosiva.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Lamborghini Urus Performante', 'Lamborghini', 2023, 4100000, 4500, 'https://www.topgear.com/sites/default/files/2022/10/1-Lamborghini-Urus-Performante.jpg', 'https://hips.hearstapps.com/hmg-prod/images/2023-lamborghini-urus-performante-0020-644a6d4b18828.jpg', null, 'https://s2-autoesporte.glbimg.com/hiObnt0K_Q6QkKyOGqk-UE_v0zs=/0x0:1800x1012/924x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_cf9d035bf26b4646b105bd958f32089d/internal_photos/bs/2022/Q/8/Bb51JTSmuPuDs4enaVtg/lamborghini-urus-performante-01.jpg', 'Rio de Janeiro, Rio de Janeiro', 'Novos', 'O Super SUV que elevou o padrão de desempenho. Este Lamborghini Urus Performante 2023, com apenas 4.500 km, é a expressão máxima de potência, design e versatilidade.', 'O Urus Performante não é apenas um SUV; é um Lamborghini com alma de superesportivo. Seu motor V8 biturbo foi aprimorado para 666 cv, combinado com uma redução de peso e aerodinâmica otimizada.');
INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Porsche 911 GT3 RS', 'Porsche', 2022, 2350000, 6000, 'https://cdn.motor1.com/images/mgl/9mQYrl/s1/porsche-911-gt3-rs-br.jpg', 'https://images-porsche.imgix.net/-/media/E8BA5C66A83F4F72A7D58B1018912116_01B1D7CA789B435E8B3F0730265FC611_CZ23V20OX0007-911-gt3-rs-driving-rear?w=1759&q=85&auto=format', 'https://images-porsche.imgix.net/-/media/D41AD8E7D55E45BDA5CFE8651F497A74_145CD2F719DF4CC88352C7DC0B71C560_CZ23V20OX0038-911-gt3-rs-side?w=2560&h=1440&q=45&crop=faces%2Centropy%2Cedges&auto=format', 'https://hips.hearstapps.com/hmg-prod/images/2025-porsche-911-gt3-104-67127826cc9ba.jpg?crop=0.652xw:0.489xh;0.227xw,0.301xh&resize=1200:*', 'São Paulo, São Paulo', 'Usados', 'Nascido nas pistas para dominar as ruas. Este Porsche 911 GT3 RS 2022, com 6.000 km, é a síntese da engenharia de competição, oferecendo uma experiência de condução sem filtros.', 'Com um motor boxer de 6 cilindros aspirado que gira a 9.000 rpm e uma aerodinâmica radicalmente otimizada, incluindo uma asa traseira com DRS, este GT3 RS é o mais próximo de um carro de corrida legalizado para as ruas.');
