INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Ferrari F8 Tributo', 'Ferrari', 2021, 3950000, 7500, 'https://image.webmotors.com.br/_fotos/anunciousados/gigante/2024/202412/20241216/ferrari-f8-tributo-3.9-v8-turbo-gasolina-f1dct-wmimagem17124182165.jpg', 'https://cdn.autopapo.com.br/box/uploads/2019/03/01115959/ferrari-f8-triturbo-5-732x488.jpg', null, null, 'Brasília, Distrito Federal', 'Usados', 'Uma homenagem ao motor V8 mais potente da história da Ferrari. Este F8 Tributo 2021, com 7.500 km, é a expressão máxima do berlinetta de dois lugares com motor central-traseiro.', 'O F8 Tributo incorpora a experiência da Ferrari nas pistas, oferecendo um desempenho extraordinário com 720 cv e uma dirigibilidade acessível.');
