INSERT INTO carro (titulo, marca, ano, preco, quilometragem, image_url, image_url1, image_url2, image_url3, localizacao, tipo, descricao_curta, descricao_longa) VALUES ('Ford Mustang Shelby GT500', 'Ford', 2020, 1350000, 9500, 'https://i0.statig.com.br/bancodeimagens/imgalta/3e/y7/wt/3ey7wt6ajs83vzfdoerez1u8r.jpg', 'https://cdn.jdpower.com/JDPA_2020-Ford-Mustang-Shelby-GT500-Blue-White-Stripes-Carbon-Fiber-Track-Pack.jpg', 'https://revistacarro.com.br/wp-content/uploads/2019/08/Mustang-GT-500-2.jpg', null, 'Recife, Pernambuco', 'Usados', 'O Mustang mais potente já produzido em série. Este Shelby GT500 2020 com 9.500 km é um ícone americano, com um motor V8 supercharged que entrega uma força brutal.', 'Fruto da colaboração entre a Ford Performance e Carroll Shelby, o GT500 é um muscle car projetado para as pistas. Seu motor "Predator" V8 de 5.2 litros produz 771 cv, gerenciado por uma transmissão de dupla embreagem de 7 velocidades.');
